import React from 'react'
import { useNavigate } from 'react-router'
import { Helmet } from 'react-helmet'
import './styles.css'
import Axios from 'axios'

let userId

function Login() {
  const [Username, setUserName] = React.useState('')
  const [password, setPassword] = React.useState('')
  const [userId, setUserId] = React.useState('')

  const navigate = useNavigate()

  function handlechange(event) {
    const { name, value } = event.target

    name === 'Username' ? setUserName(value) : setPassword(value)
  }

  const loginUser = () => {
    Axios.post('http://localhost:3001/api/loginUser', {
      Username: Username,
      password: password,
    }).then((e) => {
      // console.log(e['data'])

      //=================================================
      // if e['data'] == int // I mean a number
      // user can login
      // the number is the userId
      // else if e['data'] == 'NOT FINE'
      // no such username and pass combination
      //=================================================

      if (e['data'] != 'NOT FINE') {
        userId = setUserId(e['data'])

        navigate('./Home')
      } else {
        //handle Error
        //No such user
      }
    })
  }

  function handleClick(event) {
    loginUser()

    event.preventDefault()
  }

  function goToRegister(event) {
    navigate('./Register')
    event.preventDefault()
  }

  return (
    <div className='container' id='c1'>
      <Helmet>
        <link rel='stylesheet' href='login.css' />
      </Helmet>

      <img
        className='circle-img'
        src='data:image/jpeg;base64, '
        alt='avatar_img'
      />
      <h2>Task it : task managment made easy!</h2>
      <form className='form'>
        <input
          onChange={handlechange}
          name='Username'
          type='text'
          placeholder='Username'
        />
        <input
          onChange={handlechange}
          name='Password'
          type='password'
          placeholder='Password'
          autoComplete=''
        />

        <button onClick={handleClick} type='submit'>
          login
        </button>

        <p>not logged in ........? </p>

        <button onClick={goToRegister}>Register</button>
      </form>
    </div>
  )
}

export default Login
export { userId }
