import React,{useState} from 'react'

// note component

 

function Note(props) {

  
const [editable,setEditable]=useState(false)
const [toggle,setToggle]=useState("EDIT")

  function handleClick() {
    props.onDelete(props.index)
  }

function edit(event){

if (event.target.name ==="EDIT") {
 setToggle("OK") 
 setEditable(true);
}
else{
 props.onEdit(props.index)
 setToggle("EDIT")
 setEditable(false);
}

 



 
console.log("edit for note has been initiated")


  }

  return (
    <div className='note'>
      <h1 name="title" suppressContentEditableWarning={true}  contentEditable={editable}  >{props.title}</h1>
      <p name="description" suppressContentEditableWarning={true} contentEditable={editable}>{props.content}</p>
      <p name="due_date" suppressContentEditableWarning={true} contentEditable={editable}>{props.date}</p>
      <p name="priority" suppressContentEditableWarning={true} contentEditable={editable}>{props.priority}</p>
     
      <button onClick={handleClick}>DELETE</button>
      <button className='edit' name={toggle} onClick={edit}>{toggle+"  "}</button>
    </div>
  )
}

export default Note
