import React from 'react';
import "../css/dropdown.css"
 


function Filter(){

    const [state ,setState]=React.useState(false);

    function drop(){
    
    setState(true);
    
    
    
    }
    function hide(){
    
        setState(false);
        
        
        
        }
        
 
 
return(

 

  <div className='menu' onMouseEnter={drop}  onMouseOut={hide}>
        Filter
 {state &&( <ul onMouseEnter={drop}>

<li>in progress</li>
<li>done</li>
<li>upcoming</li>
</ul>

)}
  
  
  </div>






 
)





}


export default Filter
