import React,{useState,useRef} from 'react'

// note component

 

function Note(props) {

const [title,setTitle]=useState("")  
const [description,setDescription]=useState("")
const [due_date,setDueDate]=useState("")
const [priority,setPriority]=useState("")
const [editable,setEditable]=useState(false)
const [toggle,setToggle]=useState("EDIT")
const titleRef=useRef()

function handleChange(event){

console.log("handle change")

if(event.target.name ==="title")
  setTitle(event.target.value)
else if(event.target.name ==="description")
  setDescription(event.target.value)
else if(event.target.name ==="due_date")
  setDueDate(event.target.value)
else
   setPriority(event.target.value)

}




  function handleClick() {
    props.onDelete(props.index)
  }

function edit(event){

if (event.target.name ==="EDIT") {
 setToggle("OK") 
 setEditable(true);
 console.log(titleRef.outerText)
}
else{
 props.onEdit(props.index,title,description,due_date,priority)
 setToggle("EDIT")
 setEditable(false);
}

 



 
console.log("edit for note has been initiated")


  }

  return (
  
    
    <div    className='note'>

     
      <h1 ref={titleRef} onChange={handleChange} value={props.title}  name="title" suppressContentEditableWarning={true}  contentEditable={editable}  >{props.title}</h1>
      <p  onChange={handleChange}  name="description" suppressContentEditableWarning={true} contentEditable={editable}>{props.content}</p>
      <p  onChange={handleChange} name="due_date" suppressContentEditableWarning={true} contentEditable={editable}>{props.date}</p>
      <p  onChange={handleChange}  name="priority" suppressContentEditableWarning={true} contentEditable={editable}>{props.priority}</p>
      
     
      <button onClick={handleClick}>DELETE</button>
      <button className='edit' name={toggle} onClick={edit}>{toggle+"  "}</button>
    </div>
  )
}

export default Note
