import React from 'react'
import NavBar from '../components/NavBar'
import Filter from '../components/Filter'
import { Helmet } from 'react-helmet'
import Note from '../components/Note'
import Axios from 'axios'
import react from 'react'

const userId=1;
 
 

function deleteNote(id) {
  // insert back end code for delete here u have task id and logged in user id

  console.log('u clicked me my id is ' + id)
}

function View() {
 
 const [posts,setPosts]=react.useState([])


react.useEffect( (userid)=>{

  Axios.post('http://localhost:3001/api/retriveTasks',{userId: userId}).then(res=> {setPosts(res.data)})

}


)



 
  return (
    <div>
      <Helmet>
        <link rel='stylesheet' href='styles.css' />
      </Helmet>

      <NavBar />
      <Filter />
      {posts.map((post) => {
        return (
          <Note
            key={post.taskid}
            index={post.taskid}
            title={post.title}
            content={post.description}
            date={post.due_date}
            onDelete={deleteNote}
          />
        )
      })}
    </div>
  )
}

export default View
