import React,{useState} from 'react'

// note component

 

function Note(props) {

  
const [editable,setEditable]=useState(false)

  function handleClick() {
    props.onDelete(props.index)
  }

function edit(){


props.onEdit(props.index)



setEditable(true);
console.log("edit for note has been initiated")


  }

  return (
    <div className='note'>
      <h1 suppressContentEditableWarning={true}  contentEditable={editable}  >{props.title}</h1>
      <p suppressContentEditableWarning={true} contentEditable={editable}>{props.content}</p>
      <p  suppressContentEditableWarning={true} contentEditable={editable}>{props.date}</p>
      <p suppressContentEditableWarning={true} contentEditable={editable}>{props.priority}</p>
     
      <button onClick={handleClick}>DELETE</button>
      <button onClick={edit}>EDIT</button>
    </div>
  )
}

export default Note
