import React from "react";

 // note component


function Note(props) {

  function handleClick(){

        props.onDelete(props.index);

  
  }
  
  
  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      <p>{props.date}</p>
      <p>{props.priority}</p>
      <button onClick={handleClick}  >DELETE</button>
    </div>
  );
}

export default Note;
