import React from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import Note from '../components/Note'
import CreateArea from '../components/CreateArea'
import { Helmet } from 'react-helmet'
import Axios from 'axios'
import NavBar from '../components/NavBar'
import Radio from '../components/Radio'

function sub(date, content, title) {
  console.log('sucess')
  Axios.post('http://localhost:3001/api/insertNote', {
    due_date: date,
    description: content,
    title: title,
  }).then(() => {
    console.log('added to db')
  })
}

function Home() {
  const [notes, setNotes] = React.useState([])

  function addNote(note) {
    console.log(note.date)
    sub(note.date, note.content, note.title)
    // const submitNote=()=>{
    //   console.log("sucess");
    //   Axios.post('http://localhost:3001/api/insert',{due_date:note.date,description:note.content,title:note.title}).then(()=>{alert("success");});
    //   console.log(submitNote);

    // };
    setNotes((prevNotes) => {
      return [...prevNotes, note]
    })
  }

  function deleteNote(id) {
    setNotes((prevNote) => {
      return prevNote.filter((noteItem, index) => {
        return index !== id
      })
    })
  }

  return (
    <div>
      <Helmet>
        <link rel='stylesheet' href='styles.css' />
      </Helmet>

      <NavBar />
      <CreateArea onAdd={addNote} />

      {notes.map((noteItem, index) => {
        return (
          <Note
            key={index}
            id={index}
            date={noteItem.date}
            title={noteItem.title}
            content={noteItem.content}
            onDelete={deleteNote}
          />
        )
      })}

      <Footer />
    </div>
  )
}

export default Home
