import React, { useContext } from 'react'
import NavBar from '../components/NavBar'
import Filter from '../components/Filter'
import { Helmet } from 'react-helmet'
import Note from '../components/Note'
import Axios from 'axios'
import react from 'react'
import { userContext } from './UserContext'
import { sorting, searching, filtering } from '../components/Filter'

function deleteNote(taskid) {
  // insert back end code for delete here u have task id and logged in user id
  let usercId = 1

  Axios.post('http://localhost:3001/api/deleteTask', {
    taskid: taskid,
    userId: usercId,
  }).then(() => {
    console.log('task removed')
    View()
  })
}

function View() {
  const { value } = useContext(userContext)
  const userId = value

  const [posts, setPosts] = react.useState([])

  react.useEffect(() => {
    Axios.post('http://localhost:3001/api/retriveTasks', {
      userId: userId,
    }).then((res) => {
      setPosts(res.data)
    })
  }, [])

  return (
    <div>
      <Helmet>
        <link rel='stylesheet' href='styles.css' />
      </Helmet>

      <NavBar />
      <Filter />

      {posts.map((post) => {
        return (
          <Note
            key={post.taskid}
            index={post.taskid}
            title={post.title}
            content={post.description}
            priority={post.priority}
            date={post.due_date}
            onDelete={deleteNote}
          />
        )
      })}
    </div>
  )
}

export default View
