  }

  return (
  
    
    <div    className='note'>

    
      <h1 ref={titleRef} onChange={handleChange} value={props.title}  name="title" suppressContentEditableWarning={true}  contentEditable={editable}  >{props.title}</h1>
      <p  ref={descriptionRef} onChange={handleChange}  name="description" suppressContentEditableWarning={true} contentEditable={editable}>{props.content}</p>
      <p ref={dueDateRef} onChange={handleChange} name="due_date" suppressContentEditableWarning={true} contentEditable={editable}>{props.date}</p>
      <p ref={priorityRef} onChange={handleChange}  name="priority" suppressContentEditableWarning={true} contentEditable={editable}>{props.priority}</p>
      
    
      <button onClick={handleClick}>DELETE</button>
      <button className='edit' name={toggle} onClick={edit}>
        {toggle + '  '}
      </button>
    </div>
  )
}

export default Note
