import React, { useContext } from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import Note from '../components/Note'
import CreateArea from '../components/CreateArea'
import { Helmet } from 'react-helmet'
import Axios from 'axios'
import NavBar from '../components/NavBar'
import { userContext } from './UserContext'
import Radio from '../components/Radio'

// sub prority too
function sub(date, content, title, priority) {
  
  Axios.post('http://localhost:3001/api/insertNote', {
    userId: localStorage.getItem('userId'),
    due_date: date,
    description: content,
    title: title,
    priority: priority,
  }).then(() => {
    console.log('added to db')
  })
}

function Home() {
  const [notes, setNotes] = React.useState([])
  const { value } = useContext(userContext)
  // value contains user id use it for submisson

  function addNote(note) {
    sub(note.date, note.content, note.title, note.priority)
    setNotes((prevNotes) => {
      return [...prevNotes, note]
    })
  }

  function deleteNote(id) {
    setNotes((prevNote) => {
      return prevNote.filter((noteItem, index) => {
        return index !== id
      })
    })
  }

  return (
    <div>
      <Helmet>
        <link rel='stylesheet' href='styles.css' />
      </Helmet>

      <NavBar />
      <CreateArea onAdd={addNote} />

      {notes.map((noteItem, index) => {
        return (
          <Note
            key={index}
            id={index}
            date={noteItem.date}
            title={noteItem.title}
            priority={noteItem.priority}
            content={noteItem.content}
            onDelete={deleteNote}
          />
        )
      })}

      <Footer />
    </div>
  )
}

export default Home
